  return (
    <>
      <Flex flexDirection="column" alignItems="center" margin={4}>
        <Heading as="h1" size="2xl" marginY="2rem">
          Projector
        </Heading>
        <main>{children}</main>
        {!home && (
          <Link href="/" color="blue.500" marginY='4rem'>
            ‚Üê Back to home
          </Link>
        )}
      </Flex>
    </>
  );
}
